import React, { useState } from "react";
import { CSSTransition } from "react-transition-group";

const Features = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    "https://example.com/image1.png",
    "https://example.com/image2.png",
    "https://example.com/image3.png"
  ];

  const nextSlide = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((currentIndex + images.length - 1) % images.length);
  };

  return (
    <div className="carrossel">
      <div className="carrossel__slides">
        {images.map((image, index) => (
          <CSSTransition
            key={index}
            classNames="slide"
            timeout={{ enter: 1000, exit: 1000 }}
            in={index === currentIndex}
            unmountOnExit
          >
            <img
              className="carrossel__slide"
              src={image}
              alt={`Imagem ${index}`}
            />
          </CSSTransition>
        ))}
      </div>
      <button className="carrossel__button carrossel__button--prev" onClick={prevSlide}>
        Anterior
      </button>
      <button className="carrossel__button carrossel__button--next" onClick={nextSlide}>
        Pr√≥ximo
      </button>
    </div>
  );
};

export default Features;
