import React, { useState, useRef } from "react";

const Features = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    ".Assets/img1.jpg",
    "https://example.com/image2.png",

  ];
  const carrosselRef = useRef(null);

  const nextSlide = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
    carrosselRef.current.style.transform = `translateX(-${(currentIndex + 1) * 100}%)`;
  };

  const prevSlide = () => {
    setCurrentIndex((currentIndex + images.length - 1) % images.length);
    carrosselRef.current.style.transform = `translateX(-${(currentIndex - 1) * 100}%)`;
  };

  return (
    <div className="carrossel">
      <div className="carrossel__slides" ref={carrosselRef}>
        {images.map((image, index) => (
          <img
            key={index}
            className="carrossel__slide"
            src={image}
            alt={`Imagem ${index}`}
          />
        ))}
      </div>
      <button className="carrossel__button carrossel__button--prev" onClick={prevSlide}>
        Anterior
      </button>
      <button className="carrossel__button carrossel__button--next" onClick={nextSlide}>
        Pr√≥ximo
      </button>
    </div>
  );
};

export default Features;
