import React, { useState } from "react";
import AboutBackgroundImage from "../Assets/about-background-image.jpg";
import Video from "../Assets/video-about.mp4";

const About = () => {
  const [showVideo, setShowVideo] = useState(false);

  const handleWatchVideoClick = () => {
    setShowVideo(true);
  };

  return (
    <div className="about-section-container">
      <div className="about-section-image-container">
        <img src={AboutBackgroundImage} alt="" />
      </div>
      <div className="about-section-text-container">
        <h1 className="primary-heading">
          Explore The Beauties Of The World
        </h1>
        <p className="primary-text">
          Lorem ipsum dolor sit amet consectetur. Non tincidunt magna non et
          elit. Dolor turpis molestie dui magnis facilisis at fringilla quam.
        </p>
        <p className="primary-text">
          Non tincidunt magna non et elit. Dolor turpis molestie dui magnis
          facilisis at fringilla quam.
        </p>
        <div className="about-buttons-container">
          <button className="secondary-button" onClick={handleWatchVideoClick}>
            Watch Video
          </button>
        </div>
      </div>
      {showVideo && (
        <div className="video-container">
          <video controls>
            <source src={Video} type="video/mp4" />
          </video>
        </div>
      )}
    </div>
  );
};

export default About;
