import React, { useState } from "react";
import imagem1 from "../Assets/img1.jpg";


const Features = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [imagem1];

  const nextSlide = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((currentIndex + images.length - 1) % images.length);
  };

  return (
    <div className="carrossel">
      <div className="carrossel__slides">
        {images.map((image, index) => (
          <img
            key={index}
            className={`carrossel__slide ${index === currentIndex ? "active" : ""}`}
            src={image}
            alt={`Imagem ${index}`}
          />
        ))}
      </div>
      <button className="carrossel__button carrossel__button--prev" onClick={prevSlide}>
        Anterior
      </button>
      <button className="carrossel__button carrossel__button--next" onClick={nextSlide}>
        Pr√≥ximo
      </button>
    </div>
  );
};

export default Features;
